import React from 'react';
import { renderCardImage } from 'utils/tools';

const CartDetail = ({ products, removeItem }) => {
    const renderItems = () => (
        products ?
                products.map((product, index) => (
                    <div className='user_product_block' key={`${product._id}-${index}`}>
                        <div className='item'>
                            <div className='image'
                                style={{
                                    background: `url(${renderCardImage(product.images)}) no-repeat`
                                }}
                            >
                            </div>
                        </div>
                        <div className='item'>
                            <h4>Product Name:</h4>
                            <div>
                                {product.brand.name} {product.model}
                            </div>
                        </div>
                        <div className='item'>
                            <h4>Price: </h4>
                            <div>
                                $ {product.price}
                            </div>
                        </div>
                        <div className='item btn'>
                            <div className='cart_remove_btn'
                                onClick={() => removeItem(index)}
                            >
                                Remove
                            </div>
                        </div>
                    </div>
                ))
            :
                null
    )

    return (
        <div>
            {renderItems()}
        </div>
    )
}

export default CartDetail;
