import {
    GET_PROD_BY_SORT
} from '../types'

export default function productsReducer(state={}, action) {
    switch(action.type) {
        case GET_PROD_BY_SORT:
            return { ...state, bySort: action.payload }
        default:
            return state;
    }
}
