import React, { useState } from 'react';
import { From, Button, Form } from 'react-bootstrap';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { getTokenCookie } from 'utils/tools';
import  Loader from 'utils/loader';

const PicUpload = () => {
    const [loading, setLoading] = useState(false);

    const formikImg = useFormik({
        initialValues: { pic: '' },
        validationSchema: Yup.object({
            pic: Yup.mixed().required('A file is required')
        }),
        onSubmit: (values) => {
            setLoading(true);

        }
    });

    return (
        <>
            { loading ?
                <Loader />
                :
                <Form onSubmit={formikImg.handleSubmit}>
                    <Form.Group>
                        <Form.File 
                            id='file'
                            name='file'
                            onChange={(event) => {
                                formikImg.setFieldValue("pic", event.target.files[0])
                            }}
                        />
                        { formikImg.errors.pic && formik.touched.pic ?
                            <div>Error</div>
                            : null
                        }

                    </Form.Group>
                </Form>
        
            }
        </>
    )
};

export default PicUpload;
