import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { getTokenCookie } from 'utils/tools';
import  Loader from 'utils/loader';

const PicUpload = () => {
    const [loading, setLoading] = useState(false);

    const formikImg = useFormik({
        initialValues: { pic: '' },
        validationSchema: Yup.object({
            pic: Yup.mixed().required('A file is required')
        }),
        onSubmit: (values) => {
            setLoading(true);
            console.log(values);
            let formData = new FormData();
            formData.append("file", values.pic);
            axios.post(``, formData, {
                headers: {
                    'content-type': 'multipart/form-data',
                    'Authorization': `Bearer ${getTokenCookie}`
                }
            }).then( response => {
                // response.data
            }). catch( error => {
                alert(error)
            }).finally(() => {
                setLoading(false);
            })
        }
    });

    return (
        <>
            { loading ?
                <Loader />
                :
                <Form onSubmit={formikImg.handleSubmit}>
                    <Form.Group>
                        <Form.Control
                            type='file' 
                            id='file'
                            name='file'
                            onChange={(event) => {
                                formikImg.setFieldValue("pic", event.target.files[0])
                            }}
                        />
                        { formikImg.errors.pic && formikImg.touched.pic ?
                            <div>Error</div>
                            : null
                        }

                    </Form.Group>
                    <Button
                        variant='secondary'
                        type='submit'
                    >
                        Upload
                    </Button>
                </Form>
        
            }
        </>
    )
};

export default PicUpload;
