import { ERROR_GLOBAL, 
    SUCCESS_GLOBAL, 
    CLEAR_NOTIFICATION,
    REMOVE_PROD
} from "store/types";

export default function notificationsReducer(state={}, action) {
    switch(action.type) {
        case ERROR_GLOBAL:
            return { ...state, error: true, msg: action.payload };
        case SUCCESS_GLOBAL:
            return { ...state, success: true, msg: action.payload };
        case CLEAR_NOTIFICATION:
            return {};
        case REMOVE_PROD:
            return { ...state, removed: true }
        default:
            return state;
    }
}
