import React, { useEffect, useReducer, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import DashboardLayout from 'components/hoc/dashboardLayout';

import { useFormik } from 'formik';
import { errorHelper } from 'utils/tools';
import Loader from 'utils/loader';
import { 
    TextField,
    Button,
    Divider,
    Select,
    MenuItem,
    FormControl,
    FormHelperText
} from '@mui/material';

import { validation } from './formValues';

const AddProduct = (props) => {
    const [loading, setLoading] = useState(false);
    const notifications = useSelector(state => state.notifications);
    const dispatch = useDispatch();

    const formik = useFormik({
        initialValues: {
            model: '',
            brand: '',
            frets: '',
            woodtype: '',
            description: '',
            price: '',
            available: '',
            shipping: false
        },
        validationSchema: validation,
        onSubmit: (values) => {
            console.log(values);
        }
    });

    return (
        <DashboardLayout title="Add Product">
            { loading ?
                <Loader />
                :
                <>
                    <form className='mt-3 article_form' onSubmit={formik.handleSubmit}>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='model'
                                label='Enter a model'
                                variant='outlined'
                                {...formik.getFieldProps('model')}
                                {...errorHelper(formik, 'model')}
                            />
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='frets'
                                label='Amount of frets'
                                variant='outlined'
                                {...formik.getFieldProps('frets')}
                                {...errorHelper(formik, 'frets')}
                            />
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='woodtype'
                                label='Enter wood type'
                                variant='outlined'
                                {...formik.getFieldProps('woodtype')}
                                {...errorHelper(formik, 'woodtype')}
                            />
                        </div>
                        <div className='form-group'>
                            <FormControl variant="outlined">
                                <h5>Select a brand</h5>
                                <Select
                                    name='brand'
                                    {...formik.getFieldProps('brand')}
                                    error={ formik.errors.brand && formik.touched.brand ?
                                    true : false }
                                >
                                    <MenuItem value="">
                                        <em>None</em>
                                    </MenuItem>
                                </Select>
                                { formik.errors.brand && formik.touched.brand ?
                                    <FormHelperText error={true}>
                                        {formik.error.brand}
                                    </FormHelperText>
                                    : null
                                }
                            </FormControl>
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='description'
                                label='Enter description'
                                variant='outlined'
                                multiline
                                rows={4}
                                {...formik.getFieldProps('description')}
                                {...errorHelper(formik, 'wooddescriptiontype')}
                            />
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='frets'
                                label='Enter frets'
                                variant='outlined'
                                {...formik.getFieldProps('frets')}
                                {...errorHelper(formik, 'frets')}
                            />
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='price'
                                label='Enter price'
                                variant='outlined'
                                {...formik.getFieldProps('price')}
                                {...errorHelper(formik, 'price')}
                            />
                        </div>
                        <div className='form-group'>
                            <TextField
                                style={{width: '100%'}}
                                name='available'
                                label='Enter avilable guitars'
                                variant='outlined'
                                {...formik.getFieldProps('available')}
                                {...errorHelper(formik, 'available')}
                            />
                        </div>
                        <Divider className='mt-3 mb-3' />
                        <div className='form-group'>
                            <FormControl variant="outlined">
                                <h5>Free Shipping</h5>
                                <Select
                                    name='shipping'
                                    {...formik.getFieldProps('shipping')}
                                    error={ formik.errors.shipping && formik.touched.shipping ?
                                    true : false }
                                >
                                    <MenuItem value={true}>Yes</MenuItem>
                                    <MenuItem value={false}>No</MenuItem>
                                </Select>
                                { formik.errors.shipping && formik.touched.shipping ?
                                    <FormHelperText error={true}>
                                        {formik.error.shipping}
                                    </FormHelperText>
                                    : null
                                }
                            </FormControl>
                            <Divider className='mt-3 mb-3' />
                            <Button
                                variant='contained'
                                color='primary'
                                type='submit'
                            >
                                Add Product
                            </Button>
                        </div>
                    </form>
                </>

            }
        </DashboardLayout>
    )
};

export default AddProduct;
