import React from 'react';
import { WavesButton } from 'utils/tools';

const SlimPromotion = ({items}) => {

    const renderPromotion = () => (
        items ?
        <div className='slim_promotion_img'
            style={{
                background: `url(${items.img})`
            }}
        >
            <WavesButton
                buttonTitle={items.buttonTitle}
                linkTo={items.buttonLink}
            />
        </div>

        : null
    )

    return (
        <div className='slim_promotion'>
            { renderPromotion() }
        </div>
    )
};

export default SlimPromotion;
